function logout(){if(confirm('Tem certeza que deseja sair do sistema?')){alert('Logout realizado com sucesso!');}}
function novoAgendamento(){alert('Redirecionando para tela de novo agendamento...');}
function novoCliente(){alert('Redirecionando para cadastro de cliente...');}
function verAgenda(){alert('Redirecionando para visualização da agenda...');window.location.href='/agenda';}
function gerarRelatorio(){alert('Redirecionando para geração de relatórios...');}
document.addEventListener('DOMContentLoaded',()=>{const navMenu=document.querySelector('.nav-menu');if(navMenu){navMenu.addEventListener('click',(e)=>{const link=e.target.closest('.nav-link');if(link){e.preventDefault();document.querySelectorAll('.nav-link').forEach(l=>l.classList.remove('active'));link.classList.add('active');const linkText=link.innerText.trim();if(linkText!=='Dashboard'){alert(`Navegando para:${linkText}`);}}});}
const statCards=document.querySelectorAll('.stat-card');if(statCards.length>0){const observer=new IntersectionObserver((entries)=>{entries.forEach(entry=>{if(entry.isIntersecting){entry.target.style.opacity='1';entry.target.style.transform='translateY(0)';}});},{threshold:0.1});statCards.forEach(card=>{card.style.opacity='0';card.style.transform='translateY(20px)';card.style.transition='all 0.6s ease';observer.observe(card);});}
function updateStats(){const consultasTodayElement=document.querySelector('.stat-value');if(consultasTodayElement){let currentValue=parseInt(consultasTodayElement.textContent);if(Math.random()>0.7){const change=Math.floor(Math.random()*3)-1;const newValue=Math.max(0,currentValue+change);consultasTodayElement.textContent=newValue;consultasTodayElement.style.transform='scale(1.1)';setTimeout(()=>{consultasTodayElement.style.transform='scale(1)';},200);}}}
setInterval(updateStats,30000);});
